namespace Stockpoint.Shared.Services
{
    public class AppStateService
    {
        private string _currentPage = "Inicio";
        private string _sysToken = "token_predeterminado"; // Valor inicial
        private List<MenuItem> _menuData;
        public string CurrentPage
        {
            get => _currentPage;
            set
            {
                if (_currentPage != value)
                {
                    _currentPage = value;
                    OnStateChanged?.Invoke();
                }
            }
        }

        public List<MenuItem> MenuData
        {
            get => _menuData;
            set
            {
                _menuData = value;
                NotifyStateChanged();
            }
        }
        public string SysToken
        {
            get => _sysToken;
            set
            {
                if (_sysToken != value)
                {
                    _sysToken = value;
                    OnStateChanged?.Invoke();
                }
            }
        }

        public event Action OnStateChanged;
        private void NotifyStateChanged() => OnStateChanged?.Invoke();
    }

