        public string GetSvgIcon(string path1, string path2 = null, string path3 = null)
        {
            var svg = $@"<svg xmlns='http://www.w3.org/2000/svg' width='16' height='16' fill='var(--primary-color)' class='bi bi-gear' viewBox='0 0 16 16'>
            <path d='{path1}' />";

            if (!string.IsNullOrEmpty(path2))
            {
                svg += $@"<path d='{path2}' />";
            }
            if (!string.IsNullOrEmpty(path3))
            {
                svg += $@"<path d='{path3}' />";
            }

            svg += "</svg>";
            return svg;
        }
    }


    public class IconosService2
    {
        private readonly Dictionary<string, string> _iconCache = new();

        public string obtenerIconos(string nombreIcono)
        {
            if (string.IsNullOrWhiteSpace(nombreIcono))
                return GetDefaultIcon();

            // Usar cache para mejorar rendimiento
            if (_iconCache.TryGetValue(nombreIcono, out var cachedIcon))
                return cachedIcon;

            var icon = nombreIcono.ToLower() switch
