        public string GetSvgIcon(string path1, string path2 = null, string path3 = null)
        {
            // Cambiado viewBox a '0 0 16 16'
            var svg = $@"<svg xmlns='http://www.w3.org/2000/svg' width='16' height='16' fill='currentColor' viewBox='0 0 16 16'>
        <path d='{path1}' />";

            if (!string.IsNullOrEmpty(path2))
            {
                svg += $@"<path d='{path2}' />";
            }
            if (!string.IsNullOrEmpty(path3))
            {
                svg += $@"<path d='{path3}' />";
            }

            svg += "</svg>";
            return svg;
        }
